                                                        XmNwidth, 25, NULL);

                XtAddCallback(box, XmNactivateCallback, Callback, NULL);

                XtRealizeWidget(top);
                while (!stoploop)
                {
                        XEvent event;
                        XtAppNextEvent(app, &event);
